import React, {Component} from 'react';
import Grid from "@material-ui/core/Grid";
import withStyles from "@material-ui/core/styles/withStyles";
import style from "./style";
import Paper from "@material-ui/core/Paper";
import ButtonGroup from "@material-ui/core/ButtonGroup";
import Button from "@material-ui/core/Button";
import Typography from "@material-ui/core/Typography";
import Slider from "@material-ui/core/Slider";
import FormControl from "@material-ui/core/FormControl";
import InputLabel from "@material-ui/core/InputLabel";
import Select from "@material-ui/core/Select";
import MenuItem from "@material-ui/core/MenuItem";
import Divider from "@material-ui/core/Divider";

class Home extends Component {
    constructor() {
        super();
        this.state={
            value: [20, 37],
            veg: true,
            nonVeg: false,
            pizzaBase: '',
            pizzaType: ''
        }
    }

    handleChange = (event, newValue) => {
        console.log(event);
        console.log(newValue);
        this.setState({
            value: newValue
        });
    };

    handleChangeDropDowns = (event) => {
        console.log(event);
        console.log(event.target.value);
        this.setState({
            [event.target.name]: event.target.value
        });
    };

    marks = [
        {
            value: 100,
            label: '₹ 100',
        },
        {
            value: 200,
            label: '₹ 200',
        },
        {
            value: 300,
            label: '₹ 300',
        },
        {
            value: 400,
            label: '₹ 400',
        },
        {
            value: 500,
            label: '₹ 500',
        },
        {
            value: 600,
            label: '₹ 600',
        },
    ];

    valuetext = () => {
        return `₹ ${this.state.value}`;
    };

    setVeg=()=>{
        this.setState({
            veg: true,
            nonVeg: false
        })
    };

    setNonVeg=()=>{
        this.setState({
            veg: false,
            nonVeg: true
        })
    };

    render() {
        const { classes } = this.props;

        console.log(this.state);

        return (
            <div className={classes.root}>
                <Grid container>
                    <Grid item xs={12}>
                        <div className={classes.spacer}>
                        </div>
                    </Grid>
                    <Grid item
                          xl={3}
                          lg={3}
                          md={3}
                          xs={12}
                          style={{textAlign:"center"}}
                    >
                        <div className={classes.paddingClass}>
                            <Paper className={classes.paddingClass}>
                                <div className={classes.internalPadding}>
                                    <ButtonGroup size="small" aria-label="small outlined button group">
                                        <Button onClick={this.setVeg} color={this.state.veg ? "primary" : ""}  variant={this.state.veg ? "contained" : "outlined"}>Veg</Button>
                                        <Button onClick={this.setNonVeg}color={this.state.nonVeg ? "primary" : ""}  variant={this.state.nonVeg ? "contained" : "outlined"}>Non-Veg</Button>
                                    </ButtonGroup>
                                </div>
                                <div className={classes.internalPadding}>
                                    <Typography id="range-slider" gutterBottom>
                                        Price Range
                                    </Typography>
                                    <Slider
                                        value={this.state.value}
                                        step={100}
                                        min={100}
                                        max={600}
                                        name={"priceFilter"}
                                        onChange={this.handleChange}
                                        valueLabelDisplay="auto"
                                        aria-labelledby="range-slider"
                                        getAriaValueText={this.valuetext}
                                        style={{width: '70%'}}
                                        marks={this.marks}
                                    />
                                </div>
                                <div className={classes.internalPadding}>
                                    <FormControl variant="outlined" className={classes.formControl}>
                                        <InputLabel id="demo-simple-select-outlined-label">Pizza Base</InputLabel>
                                        <Select
                                            labelId="demo-simple-select-outlined-label"
                                            id="demo-simple-select-outlined"
                                            value={this.state.pizzaBase}
                                            name={"pizzaBase"}
                                            onChange={this.handleChangeDropDowns}
                                            label="Pizza Base"
                                        >
                                            <MenuItem value="">
                                                <em>None</em>
                                            </MenuItem>
                                            <MenuItem value={10}>Thin Crust</MenuItem>
                                            <MenuItem value={20}>Regular base</MenuItem>
                                            <MenuItem value={30}>Flat bread</MenuItem>
                                            <MenuItem value={30}>Multigrain</MenuItem>
                                        </Select>
                                    </FormControl>
                                </div>
                                <div className={classes.internalPadding}>
                                    <FormControl variant="outlined" className={classes.formControl}>
                                        <InputLabel id="demo-simple-select-outlined-label">Pizza Type</InputLabel>
                                        <Select
                                            labelId="demo-simple-select-outlined-label"
                                            id="demo-simple-select-outlined"
                                            value={this.state.pizzaType}
                                            name={"pizzaType"}
                                            onChange={this.handleChangeDropDowns}
                                            label="Pizza Type"
                                        >
                                            <MenuItem value="">
                                                <em>None</em>
                                            </MenuItem>
                                            <MenuItem value={10}>Cheese burst</MenuItem>
                                            <MenuItem value={20}>Cheese topping</MenuItem>
                                            <MenuItem value={30}>No cheese</MenuItem>
                                        </Select>
                                    </FormControl>
                                </div>
                            </Paper>
                        </div>
                    </Grid>
                    <Grid item
                          xl={9}
                          lg={9}
                          md={8}
                          xs={12}
                    >
                        <div className={classes.paddingClass}>
                            <Paper className={classes.paddingClass}>
                                <div className={classes.internalPadding}>
                                    <Grid container spacing={3}>
                                        <Grid item xs={4}>
                                            <div style={{textAlign:"center"}}>
                                                <img
                                                    alt="Select file"
                                                    className={classes.image}
                                                    src="/pizzaveg1.jpg"
                                                />
                                            </div>
                                        </Grid>
                                        <Grid item xs={8}>
                                            <Paper className={classes.paper}>
                                                <Typography variant={"h5"} gutterBottom>
                                                    Batman Pizza
                                                </Typography>
                                                <ol style={{listStyleType: "disc"}}>
                                                    <li >One</li>
                                                    <li style={{marginTop: '10px'}}>Two</li>
                                                    <li style={{marginTop: '10px'}}>Three</li>
                                                </ol>
                                                <Typography variant={"button"} gutterBottom>
                                                    ₹ 300.00
                                                </Typography>
                                                <Typography variant={"h5"} gutterBottom>
                                                    Batman Pizza
                                                </Typography>
                                            </Paper>
                                        </Grid>
                                    </Grid>
                                    <Divider style={{marginTop: '2%'}} variant="middle" />
                                </div>
                            </Paper>
                        </div>
                    </Grid>
                </Grid>
            </div>
        );
    }
}

export default withStyles(style)(Home);
