import axios from 'axios'

import AxiosMockAdapter from 'axios-mock-adapter';
import React from "react";

const instance = new AxiosMockAdapter(axios, { delayResponse: 2000 });

instance.onGet('/pizza/all').reply(200, {
    veg: [
        {
            name: 'Batman Pizza',
            crust: 'Thin',
            cheese: 'Burst',
            price: '300.00',
            image: "/pizzaveg1.jpg"
        },
        {
            name: 'Iron Man Pizza',
            crust: 'Regular',
            cheese: 'Burst',
            price: '500.00',
            image: "/pizzaveg1.jpg"
        },
        {
            name: 'Thor Pizza',
            crust: 'Flat Bread',
            cheese: 'Topping',
            price: '200.00',
            image: "/pizzaveg1.jpg"
        },
        {
            name: 'Rainy Pizza',
            crust: 'Multigrain',
            cheese: 'No',
            price: '500.00',
            image: "/pizzaveg1.jpg"
        },
    ],
    nonVeg: [
        {
            name: 'Loki Pizza',
            crust: 'Thin',
            cheese: 'Burst',
            price: '600.00',
            image: "/nonveg.jpg"
        },
        {
            name: 'Thanos Pizza',
            crust: 'Regular',
            cheese: 'Burst',
            price: '500.00',
            image: "/nonveg.jpg"
        },
        {
            name: 'Nazi Pizza',
            crust: 'Flat Bread',
            cheese: 'Topping',
            price: '200.00',
            image: "/nonveg.jpg"
        },
    ],
});

console.log(instance);
instance.onGet("/pizza/all", { params: { crust: "thin" } }).reply(200, {
    veg: [
        {
            name: 'Batman Pizza',
            crust: 'Thin',
            cheese: 'Burst',
            price: '300.00',
            image: "/pizzaveg1.jpg"
        },
    ],
    nonVeg: [
        {
            name: 'Loki Pizza',
            crust: 'Thin',
            cheese: 'Burst',
            price: '600.00',
            image: "/nonveg.jpg"
        },
    ],
});
